const FACTIONS = {
  'ORE': {
    'image': 'data:image/png;base64,',
  },
  'ABYSS': {
    'image': 'data:image/png;base64,',
  },
  'TRENCH': {
    'image': 'data:image/png;base64,',
  },
  'MACHINE': {
    'image': 'data:image/png;base64,',
  },
  'LUX': {
    'image': 'data:image/png;base64,',
  },
  'FAERIES': {
    'image': 'data:image/png;base64,',
  },
  'No faction':  {
    'image': 'https://play.crabada.com/static/media/icon_faction_neutral.4b04c12e.png',
  }
}
const CHECKER_IMG = 'http://www.clker.com/cliparts/2/k/n/l/C/Q/transparent-green-checkmark-md.png'
const buttonSound = new Audio('https://play.crabada.com/static/media/hover-sound.1f0c2dae.mp3')
let LOOTER_BP = 0

document.addEventListener('DOMContentLoaded', function() {
  //Plays a sound if audio setting is on
  function playSound(sound) {
    if (localStorage.getItem('s_audio') == 'true') {
      sound.currentTime = 0
      sound.play()
    }
  }

  //Starts script
  function startClicker() {
    playSound(buttonSound)
    chrome.tabs.query({active: true, currentWindow: true}, tabs => {
      chrome.tabs.sendMessage(
        tabs[0].id,
        {
          'action': 'startClicker',
          'looterFaction': localStorage.getItem('looterFaction'),
          'looterBP': localStorage.getItem('looterBP'),
          'maxRevenge': localStorage.getItem('maxRevenge'),
          'minRevenge': localStorage.getItem('minRevenge'),
          'minBPDiff': localStorage.getItem('minBPDiff'),
          's_ignoreFirstMines': localStorage.getItem('s_ignoreFirstMines'),
          's_audio': localStorage.getItem('s_audio'),
          's_volume': localStorage.getItem('s_volume') / 100,
        },
        receiveAnswer
      )
    })
  }

  //Stops script
  function stopClicker() {
    playSound(buttonSound)
    chrome.tabs.query({active: true, currentWindow: true}, tabs => {
      chrome.tabs.sendMessage(tabs[0].id, {'action': 'stopClicker'}, receiveAnswer)
    })
  }

  //Starts timer on the Crabada page
  function startTimer() {
    playSound(buttonSound)
    chrome.tabs.query({active: true, currentWindow: true}, tabs => {
      chrome.tabs.sendMessage(
        tabs[0].id,
        {
          'action': 'startTimer',
          'timerTime': localStorage.getItem('timerTime'),
          's_ignoreFirstMines': localStorage.getItem('s_ignoreFirstMines'),
          's_audio': localStorage.getItem('s_audio'),
          's_volume': localStorage.getItem('s_volume') / 100,
        },
        receiveAnswer
      )
    })
  }

  //Stops timer on the Crabada page
  function stopTimer() {
    playSound(buttonSound)
    chrome.tabs.query({active: true, currentWindow: true}, tabs => {
      chrome.tabs.sendMessage(tabs[0].id, {'action': 'stopTimer'}, receiveAnswer)
    })
  }

  //Create a faction button
  function createFactionDiv(factionsList, faction) {
    const factionDiv = document.createElement('div')
    factionDiv.className = 'factionContainer'
    factionsList.append(factionDiv)

    const factionImg = document.createElement('img')
    factionImg.src = FACTIONS[faction].image
    factionImg.alt = faction
    factionImg.draggable = false
    factionImg.className = 'factionImg'
    factionDiv.append(factionImg)

    const checkImg = document.createElement('img')
    checkImg.src = CHECKER_IMG
    checkImg.alt = 'checked'
    checkImg.draggable = false
    checkImg.className = 'check'
    factionDiv.append(checkImg)

    const looter_faction = localStorage.getItem('looterFaction')
    if (looter_faction) {
      if (looter_faction == faction)
        checkImg.style.display = 'block'
      else
        checkImg.style.display = 'none'
    }

    factionDiv.addEventListener('click', () => {
      playSound(buttonSound)
      Array.from(document.getElementById('factionList').children).forEach(faction => {
        faction.getElementsByClassName('check')[0].style.display = 'none'
      })
      checkImg.style.display = 'block'
      localStorage.setItem('looterFaction', faction)
    })
  }

  //Fills the factions div
  function fillFactionsList() {
    const factionList = document.getElementById('factionList')
    Object.keys(FACTIONS).forEach(faction => createFactionDiv(factionList, faction))
  }

  //Catches clicker script's result
  function receiveAnswer(msg) {
    Array.from(errors.children).forEach(error => {
      error.style.display = 'none'
    })
    if ('OK' == msg)
    {
      document.getElementById('OK').style.display = 'block'
      if (localStorage.getItem('s_close') == 'true')
        close()
    }
    else if ('NO_FACTION RUNNING NO_BP NO_MAX_REVENGE NO_MIN_REVENGE NO_MIN_BP_DIFF STOPPED NOT_RUNNING'.includes(msg))
      document.getElementById(msg).style.display = 'block'
    else if (window.chrome.runtime.lastError || msg == 'KO')
      document.getElementById('KO').style.display = 'block'
  }

  //Updates "ignore first x mines" setting
  function s_updateIgnoreFirstMines() {
    localStorage.setItem('s_ignoreFirstMines', s_ignoreFirstMines.value)
    s_ignoreFirstMinesText.textContent = s_ignoreFirstMines.value
  }

  //Toggles "close extension" setting
  function s_closeExtensionToggle() {
    localStorage.setItem('s_close', s_toggleClose.checked)
  }

  //Toggles "audio" setting
  function s_audioToggle() {
    localStorage.setItem('s_audio', s_audio.checked)
  }

  //Updates "volume" setting
  function s_updateVolume() {
    localStorage.setItem('s_volume', s_volume.value)
    buttonSound.volume = s_volume.value / 100
    s_volumeText.textContent = s_volume.value
  }

  //Toggles settings div
  function toggleSettings() {
    const settingsDiv = document.getElementById('settingsContainer')
    if (settingsDiv.hidden)
      settingsDiv.hidden = false
    else
      settingsDiv.hidden = true
  }

  //Init popup
  fillFactionsList()
  document.getElementById('startButton').addEventListener('click', startClicker)
  document.getElementById('stopButton').addEventListener('click', stopClicker)
  const BPInput = document.getElementById('BPInput')
  BPInput.addEventListener('input', e => localStorage.setItem('looterBP', e.target.value))
  BPInput.value = localStorage.getItem('looterBP')
  const maxRevengeInput = document.getElementById('maxRevengeInput')
  maxRevengeInput.addEventListener('input', e => localStorage.setItem('maxRevenge', e.target.value))
  maxRevengeInput.value = localStorage.getItem('maxRevenge')
  const minRevengeInput = document.getElementById('minRevengeInput')
  minRevengeInput.addEventListener('input', e => localStorage.setItem('minRevenge', e.target.value))
  minRevengeInput.value = localStorage.getItem('minRevenge')
  const minBPDiff = document.getElementById('minBPDiff')
  minBPDiff.addEventListener('input', e => localStorage.setItem('minBPDiff', e.target.value))
  minBPDiff.value = localStorage.getItem('minBPDiff')
  
  const timerTime = document.getElementById('timerTime')
  timerTime.addEventListener('input', e => localStorage.setItem('timerTime', e.target.value))
  timerTime.value = localStorage.getItem('timerTime')
  document.getElementById('startTimer').addEventListener('click', startTimer)
  document.getElementById('stopTimer').addEventListener('click', stopTimer)

  //Settings
  document.getElementById('settingsToggle').addEventListener('click', toggleSettings)

  const s_ignoreFirstMines = document.getElementById('s_ignoreFirstMines')
  s_ignoreFirstMines.addEventListener('click', s_updateIgnoreFirstMines)
  s_ignoreFirstMines.addEventListener('mousemove', s_updateIgnoreFirstMines)
  const s_ignoreFirstMinesValue = localStorage.getItem('s_ignoreFirstMines')
  if (s_ignoreFirstMinesValue)
    s_ignoreFirstMines.value = s_ignoreFirstMinesValue
  const s_ignoreFirstMinesText = document.getElementById('s_ignoreFirstMinesText')
  s_ignoreFirstMinesText.textContent = s_ignoreFirstMines.value
  s_updateIgnoreFirstMines()

  const s_toggleClose = document.getElementById('s_toggleClose')
  s_toggleClose.addEventListener('click', s_closeExtensionToggle)
  if (localStorage.getItem('s_close') == 'true')
    s_toggleClose.checked = true
  else
    s_toggleClose.checked = false

  const s_audio = document.getElementById('s_audio')
  s_audio.addEventListener('click', s_audioToggle)
  if (localStorage.getItem('s_audio') == 'false')
    s_audio.checked = false
  else {
    localStorage.setItem('s_audio', 'true')
    s_audio.checked = true
  }

  const s_volume = document.getElementById('s_volume')
  s_volume.addEventListener('click', s_updateVolume)
  s_volume.addEventListener('mousemove', s_updateVolume)
  const s_volumeValue = localStorage.getItem('s_volume')
  if (s_volumeValue)
    s_volume.value = s_volumeValue
  const s_volumeText = document.getElementById('s_volumeText')
  s_volumeText.textContent = s_volume.value
  s_updateVolume()
})
